import "core-js/modules/es.array.push.js";
import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.filter.js";
import "core-js/modules/esnext.iterator.find.js";
import pageheader from "@/components/page-header.vue";
import Swal from "sweetalert2";
import CryptoJS from 'crypto-js';
import apiClient from "@/plugins/axios";
import common from "@/components/common/JavaScripCommon";
export default {
  name: "ROOM",
  components: {
    pageheader
  },
  data() {
    return {
      StatusFee: [{
        title: 'Chưa thanh toán',
        value: false
      }, {
        title: 'Đã thanh toán',
        value: true
      }],
      StatusRoom: [{
        title: 'Còn trống',
        value: false
      }, {
        title: 'Đã cho thuê',
        value: true
      }],
      headersTable: [{
        title: 'STT',
        value: 'stt',
        sortable: true
      }, {
        title: 'Số phòng',
        value: 'numberOfRoom',
        sortable: true
      }, {
        title: 'Khách hàng',
        value: 'customerName',
        sortable: true
      }, {
        title: 'Số người ở tối da',
        value: 'noPStaying',
        sortable: true
      }, {
        title: 'Giá',
        value: 'priceRoom',
        sortable: true
      }, {
        title: 'Hành đồng',
        value: 'actions',
        sortable: false
      }],
      viewdialogDetail: false,
      titleDialog: '',
      viewdialogEdit: false,
      form: false,
      towerId: 0,
      RoomData: [],
      selectRoom: {},
      message: '',
      snackbar: false,
      snackbarColor: '',
      checkout: {},
      viewdialogCheckOut: false,
      viewdialogChangeRoom: false,
      contractData: [],
      towerData: [],
      changeRoom: {
        services: [{
          serviceId: '',
          price: '',
          number: 1
        }]
      },
      roomDataChange: [],
      searchStatusRoom: null,
      searchName: '',
      searchRoomName: '',
      servicedata: [],
      previewUrls: []
    };
  },
  created() {
    const idtower = this.$route.params.idtower;
    const DecodingIdTower = CryptoJS.enc.Utf8.stringify(CryptoJS.enc.Base64.parse(idtower));
    this.towerId = DecodingIdTower;
    this.GetRoomByTowerId();
  },
  computed: {
    filteredRooms() {
      return this.RoomData.filter(room => {
        // Lọc theo trạng thái phí
        // const matchesStatusFee = this.selectedStatusFee
        // ? room.statusFee === this.selectedStatusFee
        // : true;

        // Lọc theo trạng thái phòng
        const matchesStatusRoom = this.searchStatusRoom !== undefined && this.searchStatusRoom !== null ? this.searchStatusRoom === true && room.customerName && room.customerName.trim() !== "" ||
        // Đã cho thuê
        this.searchStatusRoom === false && (!room.customerName || room.customerName.trim() === "") // Còn trống
        : true;

        // Lọc theo tên khách hàng
        const matchesSearchName = this.searchName ? room.customerName?.toLowerCase().includes(this.searchName.toLowerCase()) : true;

        // Lọc theo số phòng
        const matchesSearchRoomName = this.searchRoomName ? room.numberOfRoom?.toLowerCase().includes(this.searchRoomName.toLowerCase()) : true;
        return matchesStatusRoom && matchesSearchName && matchesSearchRoomName;
      });
    }
  },
  methods: {
    async GetRoomByTowerId() {
      await apiClient.get(`Room/GetAllRoom/${this.towerId}`).then(reponse => {
        this.RoomData = reponse.data;
      }).catch(error => {
        this.message = "Lấy danh sách phòng bị lỗi " + error.response?.data?.message || error.message;
        this.snackbar = true;
        this.snackbarColor = 'red';
      });
    },
    EditRoom(roomId, title) {
      this.titleDialog = title;
      if (roomId === 0) {
        this.selectRoom = {};
        this.form = false;
      } else {
        apiClient.get(`Room/GetDetail?roomId=${roomId}`).then(reponse => {
          this.selectRoom = reponse.data;
        }).catch(error => {
          this.viewdialogEdit = false;
          this.snackbar = true;
          this.message = 'Lấy thông tin của phòng bị lỗi ' + error.response?.data?.message || error.message;
          this.snackbarColor = 'red';
        });
      }
    },
    async DetailRoom(roomId) {
      this.titleDialog = 'Xem chi tiết phòng';
      this.form = false;
      await apiClient.get(`Room/GetDetail?roomId=${roomId}`).then(reponse => {
        this.selectRoom = reponse.data;
      }).catch(error => {
        this.viewdialogEdit = false;
        this.snackbar = true;
        this.message = 'Lấy thông tin của phòng bị lỗi ' + error.response?.data?.message || error.message;
        this.snackbarColor = 'red';
      });
      if (this.selectRoom.isCustomer == true) {
        await apiClient.get(`Room/GetContractByRoomId?roomId=${roomId}`).then(reponse => {
          this.contractData = reponse.data;
        }).catch(error => {
          this.viewdialogEdit = false;
          this.snackbar = true;
          this.message = 'Lấy thông tin hợp đồng của phòng bị lỗi ' + error.response?.data?.message || error.message;
          this.snackbarColor = 'red';
        });
      }
    },
    deleteRoom(id, name) {
      const swalWithBootstrapButtons = Swal.mixin({
        customClass: {
          confirmButton: "btn btn-success",
          cancelButton: "btn btn-danger ml-2"
        },
        buttonsStyling: false
      });
      swalWithBootstrapButtons.fire({
        title: "Bạn có chắc chắn không?",
        text: `Bạn đang muốn xóa phòng: ${name}`,
        icon: "warning",
        confirmButtonText: "Có!",
        cancelButtonText: "Không!",
        showCancelButton: true
      }).then(confirm => {
        if (confirm.value) {
          apiClient.delete(`/Room/DeleteRoom/${id}`).then(reponse => {
            console.log(reponse);
            if (reponse.status) {
              swalWithBootstrapButtons.fire("Xóa thành công!", `Đã xóa thành công phòng: ${name}`, "success");
              this.GetRoomByTowerId();
            } else {
              swalWithBootstrapButtons.fire("Xóa không thành công", `Đã xảy ra lỗi khi xóa phòng: ${name}`, "error");
            }
          }).catch(error => {
            swalWithBootstrapButtons.fire("Xóa không thành công", ` ${error.response?.data?.message || error.message}`, "error");
          });
        } else if (/* Read more about handling dismissals below */confirm.dismiss === Swal.DismissReason.cancel) return;
      });
    },
    FormatTablePrice(price) {
      return common.formatTablePrice(price);
    },
    required(v) {
      return !!v || 'Vui lòng không để trống';
    },
    requiredNumber(v) {
      return !!v || 'Vui lòng nhập vào số';
    },
    FormatPrice() {
      this.selectRoom.priceRoom = common.formatPrice(this.selectRoom.priceRoom);
    },
    CheckOutRoom() {
      this.viewdialogDetail = false;
      this.form = true;
    },
    ChangeRoom(id) {
      this.viewdialogDetail = false;
      this.form = false;
      this.changeRoom = {
        roomIdOld: id,
        services: [{
          serviceId: '',
          price: '',
          number: 1
        }]
      };
      apiClient.get(`/Tower/GetAllTowerByLandLordId/${1}`).then(response => {
        this.towerData = response.data;
      }).catch(error => {
        this.message = 'Đã xảy ra lỗi ' + error.response?.data?.message || error.message;
        this.snackbarColor = 'red';
        this.snackbar = true;
      });
    },
    onServiceSelected(service) {
      const selectedService = this.servicedata.find(item => item.id === service.serviceId);
      if (selectedService) {
        service.price = selectedService.price;
      } else {
        service.price = ''; // Xóa giá nếu không có dịch vụ
      }
    },
    addService() {
      this.changeRoom.services.push({
        serviceId: '',
        price: '',
        number: 1
      });
    },
    closeService(index) {
      this.changeRoom.services.splice(index, 1);
    },
    createEditRoom() {
      this.selectRoom.towerid = this.towerId;
      if (this.selectRoom.id === undefined) {
        this.message = 'Thêm thành công';
        this.selectRoom.id = 0;
      } else {
        this.message = 'Sửa thành công';
      }
      if (!/^\d+$/.test(this.selectRoom.priceRoom)) this.selectRoom.priceRoom = this.selectRoom.priceRoom.replace(/[^\d]/g, '');
      apiClient.post(`/Room/CreateEditRoom`, this.selectRoom).then(response => {
        if (response.status) {
          this.snackbarColor = 'green';
          this.snackbar = true;
          this.viewdialogEdit = false;
          this.GetRoomByTowerId();
        } else {
          this.message = 'Đã xảy ra lỗi ';
          this.snackbarColor = 'red';
          this.snackbar = true;
        }
      }).catch(error => {
        this.message = 'Đã xảy ra lỗi ' + error.response?.data?.message || error.message;
        this.snackbarColor = 'red';
        this.snackbar = true;
      });
    },
    formatDate(dateString) {
      const date = new Date(dateString);
      return date.toLocaleDateString('vi-VN'); // Định dạng: dd/MM/yyyy
    },
    async filterChangeRooms() {
      await apiClient.get(`Room/GetRoomNoContract?towerID=${this.changeRoom.towerId}`).then(reponse => {
        this.roomDataChange = reponse.data;
      }).catch(error => {
        this.message = "Lấy danh sách phòng để đổi bị lỗi " + error.response?.data?.message || error.message;
        this.snackbar = true;
        this.snackbarColor = 'red';
      });
      await apiClient.get(`/Service/GetAll?towerId=${this.changeRoom.towerId}`).then(reponse => {
        this.servicedata = reponse.data;
      }).catch(error => {
        this.message = "Lấy danh sách dịch vụ bị lỗi " + error.response?.data?.message || error.message;
        this.snackbar = true;
        this.snackbarColor = 'red';
      });
    },
    btnSaveChangeRoom() {
      const selectedDate = new Date(this.changeRoom.timesChange); // Ngày người dùng chọn
      const currentDate = new Date(); // Ngày hiện tại

      // Đặt thời gian của currentDate và selectedDate về 00:00:00 để chỉ so sánh ngày
      currentDate.setHours(0, 0, 0, 0);
      selectedDate.setHours(0, 0, 0, 0);
      if (selectedDate < currentDate) {
        this.message = "Thời gian đổi đang nhỏ hơn ngày hiện tại. Bạn vui lòng chọn lại";
        this.snackbar = true;
        this.snackbarColor = 'red';
      } else {
        apiClient.put(`Room/ChangeRoom`, this.changeRoom).then(response => {
          if (response.status) {
            this.snackbarColor = 'green';
            this.snackbar = true;
            this.message = 'ĐỔi phòng thành công', this.viewdialogChangeRoom = false;
            this.GetRoomByTowerId();
          } else {
            this.message = 'Đã xảy ra lỗi ';
            this.snackbarColor = 'red';
            this.snackbar = true;
          }
        }).catch(error => {
          this.message = 'Đã xảy ra lỗi ' + error.response?.data?.message || error.message;
          this.snackbarColor = 'red';
          this.snackbar = true;
        });
      }
    },
    addCustomer(id) {
      this.$store.commit('setRoom', id);
      const encryptedId = CryptoJS.enc.Base64.stringify(CryptoJS.enc.Utf8.parse(0));
      this.$router.push({
        name: 'createEdit',
        params: {
          idcontract: encryptedId
        }
      });
    },
    handleFileChange() {
      this.previewUrls = [];
      if (this.selectedFiles) {
        for (let file of this.selectedFiles) {
          const reader = new FileReader();
          reader.onload = e => {
            this.previewUrls.push(e.target.result);
          };
          reader.readAsDataURL(file);
        }
      }
    }
  }
};
