import "core-js/modules/es.array.push.js";
import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.filter.js";
import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import pageheader from "@/components/page-header.vue";
import Swal from "sweetalert2";
import CryptoJS from 'crypto-js';
import apiClient from "@/plugins/axios";
export default {
  name: "PRODUCT-LIST",
  components: {
    pageheader
  },
  data() {
    return {
      viewdialog: false,
      titleDialog: '',
      form: false,
      headersTable: [{
        title: 'STT',
        value: 'stt',
        sortable: true
      }, {
        title: 'Tên khách hàng',
        value: 'customerName',
        sortable: true
      }, {
        title: 'Số phòng',
        value: 'numberOfRoom',
        sortable: true
      }, {
        title: 'Ngày thuê',
        value: 'startDate',
        sortable: true
      }, {
        title: 'Ngày hết hạn',
        value: 'endDate',
        sortable: true
      }, {
        title: 'Số điện thoại',
        value: 'phoneCustomer',
        sortable: true
      }, {
        title: 'Tiền cọc',
        value: 'deposit',
        sortable: true
      }, {
        title: 'Hành đồng',
        value: 'actions',
        sortable: false
      }],
      contractData: [],
      selectContract: {},
      towerId: 0,
      message: '',
      snackbar: false,
      snackbarColor: '',
      roomData: [],
      searchRoom: null,
      searchCustomerName: '',
      searchRentalDate: '',
      searchComelDate: '',
      viewdialogContractExtension: false,
      contractExtension: {},
      ViewdialogEditContractSample: false
    };
  },
  created() {
    const idtower = this.$route.params.idtower;
    const DecodingIdTower = CryptoJS.enc.Utf8.stringify(CryptoJS.enc.Base64.parse(idtower));
    this.towerId = DecodingIdTower;
    this.GetAllContractBytowerId();
    this.GetRoomDropDown();
  },
  computed: {
    filteredContracts() {
      return this.contractData.filter(contract => {
        const matchesSearchRoomName = this.searchRoom ? contract.numberOfRoom == this.searchRoom : true;

        // Lọc theo tên khách hàng
        const matchesSearchCustomerName = this.searchCustomerName ? contract.customerName?.toLowerCase().includes(this.searchCustomerName.toLowerCase()) : true;

        // Lọc theo ngày thuê
        const matchesSearchFromlDate = this.searchRentalDate ? new Date(contract.startDate).toISOString().split('T')[0] >= this.searchRentalDate : true;
        const matchesSearchComelDate = this.searchRentalDate ? new Date(contract.endDate).toISOString().split('T')[0] <= this.searchComelDate : true;
        // Kết hợp tất cả các điều kiện
        return matchesSearchRoomName && matchesSearchCustomerName && matchesSearchFromlDate && matchesSearchComelDate;
      });
    }
  },
  methods: {
    GetAllContractBytowerId() {
      apiClient.get(`Contract/GetAll/${this.towerId}`).then(reponse => {
        this.contractData = reponse.data;
      }).catch(error => {
        this.message = "Lấy danh sách hợp đồng bị lỗi " + error.response?.data?.message || error.message;
        this.snackbar = true;
        this.snackbarColor = 'red';
      });
    },
    deleteContract(id, name) {
      const swalWithBootstrapButtons = Swal.mixin({
        customClass: {
          confirmButton: "btn btn-success",
          cancelButton: "btn btn-danger ml-2"
        },
        buttonsStyling: false
      });
      swalWithBootstrapButtons.fire({
        title: "Bạn có chắc chắn không?",
        text: `Bạn đang muốn xóa hợp của phòng: ${name}`,
        icon: "warning",
        confirmButtonText: "Có!",
        cancelButtonText: "Không!",
        showCancelButton: true
      }).then(confirm => {
        if (confirm.value) {
          apiClient.delete(`/Contract/Delete/${id}`).then(reponse => {
            console.log(reponse);
            if (reponse.status) {
              swalWithBootstrapButtons.fire("Xóa thành công!", `Đã xóa thành công hợp đồng của phòng: ${name}`, "success");
              this.GetAllContractBytowerId();
            } else {
              swalWithBootstrapButtons.fire("Xóa không thành công", `Đã xảy ra lỗi khi xóa hợp đồng của phòng: ${name}`, "error");
            }
          }).catch(error => {
            swalWithBootstrapButtons.fire("Xóa không thành công", ` ${error.response?.data?.message || error.message}`, "error");
          });
        } else if (/* Read more about handling dismissals below */confirm.dismiss === Swal.DismissReason.cancel) return;
      });
    },
    DetailService(id, title) {
      this.titleDialog = title;
      apiClient.get(`/Contract/GetDetail?contractId=${id}`).then(response => {
        this.selectContract = response.data;
        console.log(response.data);
      }).catch(error => {
        this.message = "Lấy thông tin của hợp đồng bị lỗi " + error.response?.data?.message || error.message;
        this.snackbar = true;
        this.snackbarColor = 'red';
      });
    },
    required(v) {
      return !!v || 'Vui lòng không để trống';
    },
    CreateEditContract(id) {
      this.$store.commit('setRoom', null);
      const encryptedId = CryptoJS.enc.Base64.stringify(CryptoJS.enc.Utf8.parse(id));
      this.$router.push({
        name: 'createEdit',
        params: {
          idcontract: encryptedId
        }
      });
    },
    formatDate(dateString) {
      const date = new Date(dateString);
      return date.toLocaleDateString('vi-VN');
    },
    async GetRoomDropDown() {
      await apiClient.get(`/Room/GetDropDown?towerId=${this.towerId}`).then(response => {
        this.roomData = response.data;
      }).catch(error => {
        this.message = "Lấy thông tin của hợp đồng bị lỗi " + error.response?.data?.message || error.message;
        this.snackbar = true;
        this.snackbarColor = 'red';
      });
    },
    ExportWordcontract(contractId) {
      apiClient.post(`/Contract/ExportWord/${contractId}`, null, {
        responseType: "blob"
      }).then(response => {
        const fileURL = window.URL.createObjectURL(new Blob([response.data]));
        const fileLink = document.createElement("a");
        fileLink.href = fileURL;
        fileLink.setAttribute("download", "contract.docx");
        document.body.appendChild(fileLink);
        fileLink.click();
        document.body.removeChild(fileLink);
      }).catch(error => {
        this.message = "Tải hợp đồng bị lỗi: " + error.response?.data?.message || error.message;
        console.log(error.response);
        this.snackbar = true;
        this.snackbarColor = "red";
      });
    },
    ContractExtension(id, name) {
      this.contractExtension.contractId = id;
      this.titleDialog = 'Gia hạn hợp cho phòng ' + name;
    },
    btnSaveContractExtension() {
      apiClient.put(`/Contract/Extend`, this.contractExtension).then(response => {
        if (response.status) {
          const swalWithBootstrapButtons = Swal.mixin({
            customClass: {
              confirmButton: "btn btn-success",
              cancelButton: "btn btn-danger ml-2"
            },
            buttonsStyling: false
          });
          swalWithBootstrapButtons.fire({
            title: "Bạn có muốn sửa thông tin của hợp đồng không?",
            text: `Gia hạn hợp đồng thành công. Bạn có muốn sửa thông tin của hợp đồng`,
            icon: "warning",
            confirmButtonText: "Có!",
            cancelButtonText: "Không!",
            showCancelButton: true
          }).then(confirm => {
            if (confirm.value) {
              this.CreateEditContract(this.contractExtension.contractId);
            } else if (/* Read more about handling dismissals below */confirm.dismiss === Swal.DismissReason.cancel) {
              this.viewdialogContractExtension = false;
              this.message = 'Gia hạn hợp đồng thành công';
              this.snackbarColor = 'green';
              this.snackbar = true;
              this.GetAllContractBytowerId();
            }
          });
        } else {
          this.message = 'Đã xảy ra lỗi ';
          this.snackbarColor = 'red';
          this.snackbar = true;
        }
      }).catch(error => {
        this.message = 'Đã xảy ra lỗi ' + error.response?.data?.message || error.message;
        this.snackbarColor = 'red';
        this.snackbar = true;
      });
    },
    rowClass(item) {
      const today = new Date();
      const endDate = new Date(item.endDate);
      // Điều kiện màu đỏ: endDate <= ngày hiện tại
      if (endDate <= today) {
        return 'row-red';
      }

      // Điều kiện màu vàng: endDate nằm trong khoảng từ hôm nay đến 1 tháng sau
      const oneMonthLater = new Date(today);
      oneMonthLater.setMonth(today.getMonth() + 1);
      if (endDate > today && endDate <= oneMonthLater) {
        return 'row-yellow';
      }

      // Mặc định không màu
      return 'row-yellow';
    }
  }
};
