import apiClient from "@/plugins/axios";
const authModule = {
    namespaced: true,
    state: () => ({
        token: null,
    }),
    mutations: {
        setToken(state, token) {
            state.token = token;
        },
    },
    actions: {
        login({ commit }, credentials) {
            return apiClient.post('/api/auth/login', credentials)
                .then(response => {
                    const token = response.data.token;
                    commit('setToken', token); // Lưu token vào Vuex
                    localStorage.setItem('token', token); // (Tuỳ chọn) Lưu vào localStorage
                });
        },
    },
    getters: {
        isAuthenticated(state) {
            return !!state.user; // Trả về true nếu user không null
        },
    },
};
export default authModule;
