import { state } from "./modules/layout";

const authModule = {
    namespaced: true,
    state: () => ({
        token: null,
        userId: null,
    }),
    mutations: {
        setToken(state, token) {
            state.token = token;
        },
        setUser(state, userId) {
            state.userId = userId;
        },
        clearAuth(state) {
            state.token = null;
            state.userId = null;
        },
    },
    actions: {
        login({ commit }, Userformation) {
            const { token, userId } = Userformation

            // Lưu token và userId vào Vuex
            commit('setToken', token);
            commit('setUser', userId);

            // Cập nhật token vào header mặc định của axios
            //axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        },
        logout({ commit }) {
            // Xóa dữ liệu trong Vuex
            commit('clearAuth');

            // Xóa token trong localStorage
            localStorage.removeItem('token');

            // Xóa header Authorization
            delete axios.defaults.headers.common['Authorization'];

        },
        autoLogin({ commit }) {
            const token = localStorage.getItem('token');
            if (token) {
                commit('setToken', token);
                // Gọi thêm API để lấy userId nếu cần
            }
        },
        saveToken(){
            // Lưu token vào localStorage
            localStorage.setItem('token', state.token);
        }
    },
    getters: {
        isAuthenticated(state) {
            return !!state.token; // Trả về true nếu token không null
        },
        getUserId(state) {
            return state.userId;
        },
    },
};
export default authModule;
